mapper.update:
UPDATE booking
SET
booking_state = #{bookingState}
where booking_no = #{bookingNo}
and business_id = #{businessId}

mapper.delete:
DELETE FROM booking 
where booking_no = #{bookingNo}
and business_id = #{businessId}

mapper.insert:
INSERT INTO bookingok ( bookingok_count, bookingok_request, bookingok_time, bookingok_state, booking_no, business_id,deposit) 
VALUES (
    #{bookingOkCount},
    #{bookingOkRequest},
    #{bookingOkTime}, 
    #{bookingOkState},
    #{bookingNo},
    #{businessId},
    #{deposit}
);
