mapper.select:
select normal_id from normal_user WHERE normal_id = #{normalId} or nickname = #{normalId}
	union
select business_id from business_user WHERE business_id = #{normalId} or business_name = #{normalId}
	union
select root_id from root_user WHERE root_id = #{normalId}

SELECT COUNT(*) FROM normal_user WHERE nickname = #{nickName} OR normal_id = #{nickName}
	UNION
SELECT COUNT(*) FROM business_user WHERE business_id = #{nickName} OR business_name = #{nickName}
	UNION
SELECT COUNT(*) FROM root_user WHERE root_id = #{nickName} OR root_name = #{nickName}
